var missing_credits = 42;

// alle Ergebnisse vom Student mit Status bestanten holen
var credits = 0;
var gr = new GlideRecord('x_271560_campusnow_ergebnis');
gr.addQuery('u_user', current.u_student);
gr.addQuery('u_state', '3');
gr.query();

// Die Credits der Leistung der Ergebnisse addieren
while(gr.next()){
	var gr2 = new GlideRecord('x_271560_campusnow_leistung');
	gr2.addQuery('sys_id', gr.u_leistung);
	gr2.query();
	if(gr2.next()){
		credits = credits + gr2.u_credits;
	}
}

// Student holen
var gr3 = new GlideRecord('x_271560_campusnow_student');
gr3.get('u_user', current.u_student);

gs.error("Student: " + gr3.sys_id);

// Studiengang vom Student holen
var gr4 = new GlideRecord('x_271560_campusnow_studiengang');
gr4.get('sys_id', gr3.u_studiengang);

gs.error("Studiengang: " + gr4.sys_id);

// Credits vom Studiengang holen
var required_credits = parseInt(gr4.u_required_credits) - missing_credits;

gs.error("Req: " + required_credits);
gs.error("Act: " + credits);

// reicht's?
if(credits >= required_credits){
	answer = 'yes';
}else{
	answer = 'no';
}

